graphql.query:
query Users {
  users {
    id
    firstName
    lastName
    email
    accountType
  }
}

query Appointments {
  appointments {
    id
    patientId
    carerId
    start
    end
    status
    actualStart
    actualEnd
  }
}

query UserInfo($userId: ID!) {
  userInfo(userId: $userId) {
    id
    firstName
    lastName
    email
    accountType
  }
}

query CarerDashboard($userId: ID!) {
  carerDashboard(userId: $userId) {
    carer {
      userId{
          id
          firstName
          lastName
          email
      }
      postcode
      days
      notificationCount
      appointmentCount
    }
    appointments {
      id
      patientId
      carerId
      start
      end
      status
      actualStart
      actualEnd
    }
    notifications {
      notificationDate
      receiverId
      senderId
      isRead
      notificationText
    }
  }
}

query PatientDashboard($userId: ID!) {
  patientDashboard(userId: $userId) {
    patient {
      userId {
          id
          firstName
          lastName
          email
      }
      postcode
      days
      notificationCount
      appointmentCount
    }
    appointments {
      id
      patientId
      carerId
      start
      end
      status
      actualStart
      actualEnd
    }
    notifications {
      notificationDate
      receiverId
      senderId
      isRead
      notificationText
    }
  }
}

query FindPatientsByCarerSexAndDay($userId: ID!, $dayInput: DayInput) {
  findPatientsByCarerSexAndDay(userId: $userId, dayInput: $dayInput) {
    userId {
          id
          firstName
          lastName
          email
      }
      sex
      postcode
      days
      notificationCount
      appointmentCount
    }
  }

query FindPatientsByCarerSex($userId: ID!) {
  findPatientsByCarerSex(userId: $userId) {
      userId {
          id
          firstName
          lastName
          email
      }
      sex
      postcode
      days
      notificationCount
      appointmentCount
    }

}
